sqlReaderQuery.value:
@concat(
	if(
		equals(toUpper(pipeline().parameters.SrcObjectType),'QUERY'),
		if(
				equals(toUpper(pipeline().parameters.LoadType),'INITIAL'),
				pipeline().parameters.SrcObjectName,
				if(
					equals(toUpper(pipeline().parameters.SourceDeltaColumn),'NULL'),
					pipeline().parameters.SrcObjectName,
					concat(pipeline().parameters.SrcObjectName, ' AND ',pipeline().parameters.SourceDeltaColumn,'>=CONVERT(DATETIME,','''',
					formatDateTime(pipeline().parameters.LastSuccessDate,'yyyy-MM-dd HH:mm:ss')
					,'''',')'
					)
				)
			),
		concat('SELECT * FROM ',pipeline().parameters.SrcObjectName,
			if(
				equals(toUpper(pipeline().parameters.LoadType),'INITIAL'),
				'',
				if(
					equals(toUpper(pipeline().parameters.SourceDeltaColumn),'NULL'),
					'',
					concat(' WITH(NOLOCK) WHERE ',pipeline().parameters.SourceDeltaColumn,'>=CONVERT(DATETIME,','''',
					formatDateTime(pipeline().parameters.LastSuccessDate,'yyyy-MM-dd HH:mm:ss')
					,'''',')'
					)
				)
			)
		)
	)
)

select * 
from dbo.popb_responsesBase with(nolock)
where popb_responsesId in (@{pipeline().parameters.responses})
